def getfacultypages(department):
    base_url  = "https://www.iiserpune.ac.in/"
    url1 = "https://www.iiserpune.ac.in/research/department/" + department + "/people"
    url2 = "https://www.iiserpune.ac.in/research/department/" + department + "/people/faculty/adjunct-faculty"
    url3 = "https://www.iiserpune.ac.in/research/department/" + department + "/people/faculty/guest-faculty"
    url4 = "https://www.iiserpune.ac.in/research/department/" + department + "/people/faculty/visiting-faculty"
    urls = [url1 , url2 , url3 , url4]
    print(urls)
    #pages = []
    for url in urls:
        try:
            driver = driver_setup()
            driver.get(url)
        except Exception as e:
            print(e)
            sys.exit(1)
        try:
            pagesource = driver.page_source
            soup = BeautifulSoup(pagesource, 'html.parser')
            info = soup.select(' ul.people-list.people-list-rich li a')
            for a in info:
                pages.append(base_url + a['href'])
        except Exception as e:
            print(e)
    print(len(pages))
    return pages
