def getfacultyinfo(link):
    info = {}
    base_url  = "https://www.iiserpune.ac.in/"
    driver = driver_setup()


    try:
        driver.get(link)
    except Exception as e:
        print(e)
        sys.exit(1)
                
    
    try:
        pagesource = driver.page_source
        soup = BeautifulSoup(pagesource, 'html.parser')
    except:
        print(e)
        sys.exit(1)


    try:
        info['Name'] = soup.select_one('div.text h3').text

    except:
        info['Name'] = None

    try:
        info['JobTitle'] = soup.select_one('div.text p').text
    except:
        info['JobTitle'] = None

    try:
        info['Email'] = soup.select('div.pre-line-inner p')[2].text.strip()
    except:
        info['Email'] = None

    try:
        info['Phone'] = soup.select('div.pre-line-inner p')[1].text.strip()
    except:
        info['Phone'] = None
    
    try:
        info['ResearchGroupWebsite'] = soup.select_one('div.pre-line-inner p a')['href']
    except:
        info['ResearchGroupWebsite']  = None
    try:
        info['Department'] = soup.select_one('p.apply-department-seperator span').text.strip()
    except:
        info['Department'] = None
    
    try:
        info['ProfileImage'] = base_url + soup.select_one('div.name-card img')['src']
    except:
        info['ProfileImage'] = None


    info['IISERWebPage'] = link

    try:
        info['ResearchAreas'] = soup.select_one('div.pre-line-inner p').text.strip()
    except:
        info['ResearchAreas'] = None

    try:

        info['About'] = soup.select_one('div.rich-text.markdown-text p').text.strip()
    except:
        info["About"] = None
    try:

        info['ResearchAndPublications'] =[p.text + "////" for p in soup.select('div.rich-text.markdown-text p')[1:]]
    except:
        info['ResearchAndPublications'] = None

    #file = json.dumps(soup.text.replace("\n" , ""))

    return info

